function createRequest(method, id, params, httpOptions) {
    sys.logs.debug('[infura] POST from: ' + method);
    var options = httpOptions || {};
    options.body = {
        jsonrpc: "2.0",
        method: method,
        params: params || [],
        id: id
    };
    return endpoint._post(options);
}

